mapper.update:
UPDATE CONTENT
SET ct_viewcnt = ct_viewcnt + 1
WHERE ct_uid = #{ct_uid}

mapper.select:
SELECT *
FROM CONTENT
WHERE CT_UID = #{ct_uid} AND SG_ID = #{sg_id}

mapper.update:
